DROP TABLE IF EXISTS "produits";

CREATE TABLE IF NOT EXISTS "produits" (
  "id" SMALLINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" TEXT,
  "description" TEXT,
  "reference" INTEGER NOT NULL,
  "origine" TEXT,
  "price" REAL NOT NULL,
  "type" TEXT,
  "disponibilite" TEXT
);

SET client_encoding TO 'UTF8';

